import React, { useState, useEffect } from 'react';
import styles from '../styles/employe.module.scss';
import { FindAllProducts } from '../service/ProductService';

export const EmployeePage = () => {
  const [products, setProducts] = useState([]);
  const [shopingCard, setShopingcard] = useState([]);

  const findAllProducts = async () => {
    const { data } = await FindAllProducts()
    if (data?.data) {
      setProducts(data?.data)
    }
  }

  const onAddToShopingCard = (item) => {
    setShopingcard([...shopingCard, item]);
    setProducts(products.filter((product) => product._id === item._id))
  }

  useEffect(() => {
    findAllProducts()
  }, []);

  return (
    <div className="container">
      <div className={styles.title}>Employee Page</div>
      <div className={styles.employee_container}>
        <div className={styles.card}>
          {
            products ? products.map((item, index) => {
              return (
                <div key={index} onClick={onAddToShopingCard} className={styles.item}>
                  <span>{item.name}</span>
                  <span>{item.name}$</span>
                </div>
              )
            }) : null
          }
        </div>
        <div className={styles.card}>
          {
            shopingCard ? products.map((item, index) => {
              return (
                <div key={index} onClick={onAddToShopingCard} className={styles.item}>
                  <span>{item.name}</span>
                  <span>{item.name}$</span>
                </div>
              )
            }) : null
          }
        </div>
      </div>
    </div>
  )
}
