import React, { createContext, useContext, useEffect, useState } from "react";
import { Sign_In, Sign_Up } from "./service/AuthService";
import { Router } from "./Router/Router";
import { getMe } from "./service/UserService";

export const AuthData = () => useContext(AuthContext);
const AuthContext = createContext();

export function App() {
  const [user, setUser] = useState({
    name: "",
    email: "",
    isAuth: false,
  });

  const LogIn = async (user) => {
    const { data } = await Sign_In(user);
    console.log('data', data)
    if (data.data.accessToken) {
      const { email, username } = data.user;
      setUser({
        name: username,
        email,
        isAuth: true,
      });
      localStorage.setItem("token", JSON.stringify(data.accessToken));
      return data.user;
    } else {
      alert("something went wrong");
      return null;
    }
  };

  const SignUp = async (user) => {
    const { data } = await Sign_Up(user);
    if (data.user) {
      return data.user;
    } else {
      alert("something went wrong");
    }
  };

  const LogOut = () => {
    setUser({ ...user, isAuth: false });
  };

  const getCurentUser = async () => {
    const data = await getMe();
    if (data.user) {
      setUser({
        name: data.user.username,
        email: data.user.email,
        isAuth: true,
      });
      return data.user;
    }
    return null;
  };

  return (
    <AuthContext.Provider
      value={{ user, LogIn, SignUp, LogOut, getCurentUser }}
    >
      <Router />
    </AuthContext.Provider>
  );
}
