import React, { useEffect, useState } from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import { AuthData } from "../App";
import { Auth } from "../pages/Auth";
import { AdminPage } from "../pages/AdminPage";
import { EmployeePage } from '../pages/EmployeePage';
import { ErrorPageNotFound } from '../pages/ErrorPageNotFound';
import { Layout } from "../components/Layout";

const routerData = [
  {
    path: "/auth",
    name: "Auth",
    element: <Auth />,
    isPrivate: false,
  },
  {
    path: "/employee-page",
    name: "EmployeePage",
    element: <EmployeePage />,
    isPrivate: true,
    role: 'employee'
  },
  {
    path: "/admin-page",
    name: "AdminPage",
    element: <AdminPage />,
    isPrivate: true,
    role: 'administrator'
  },
];

export const Router = () => {
  const { user, getCurentUser } = AuthData();
  const [routs, setRoutes] = useState([]);
  const [render, setRender] = useState(0)

  const authGuard = async () => {
    setRender(render + 1)
    const userData = await getCurentUser();
    const rou = routerData?.filter((item) => {
      if (userData && item.isPrivate) {
        if (userData?.role === item?.role) {
          return {
            path: item.path,
            element: item.element,
          }
        }
      } else if (!userData && !item.isPrivate) {
        return {
          path: item.path,
          element: item.element,
        };
      }
    })

    setRoutes(userData ? [
      {
        path: "/",
        element: <Layout />,
        children: [
          ...rou
        ]
      }
    ] : rou);
  };

  useEffect(() => {
    if (render < 2) {
      console.log(render, 'render')
      authGuard();
    }
  }, [user]);

  const router = createBrowserRouter([
    ...routs,
    {
      path: "*",
      element: <ErrorPageNotFound />
    },
  ]);

  return <RouterProvider router={router} />;
};
