import React, { useState, useEffect } from 'react';
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import styles from '../styles/employe.module.scss';
import { FindAllProducts } from '../service/ProductService';
import { Product } from '../components/Product';


export const EmployeePage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [products, setProducts] = useState([]);

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = (item) => {
    const index = products.findIndex((e) => e.name === item.name);
    const updatedCart = [...products];
    updatedCart.splice(index, 1);
    setProducts(updatedCart);
    addToCart(item);
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, product) => total + product.price, 0);
  };

  const findAllProducts = async () => {
    const { data } = await FindAllProducts()
    if (data?.data) {
      setProducts(data?.data)
    }
  }

  useEffect(() => {
    findAllProducts()
  }, []);

  return (
    <div className="container">
      <div className={styles.title}>Employee Page</div>
      <div className={styles.employee_container}>
        <div className={styles.card}>
          {
            products ? products.map((item, index) => {
              return (
                <div key={index} className={styles.item}>
                  <span>{item.name}</span>
                  <span>{item.price}$</span>
                </div>
              )
            }) : null
          }

        </div>
        <DndProvider backend={HTML5Backend}>
          <div className={styles.card}>
            {products.map((product) => (
              <Product key={product._id} {...product} />
            ))}
            <div className={styles.droppableElement}>
              <ShoppingCart
                cartItems={cartItems}
                removeFromCart={removeFromCart}
                calculateTotal={calculateTotal}
              />
            </div>
          </div>
        </DndProvider>
      </div>
    </div>
  )
}
