import React, { useState } from "react";
import styles from "../styles/auth.module.scss";
import { Input } from "../components/Input";

export const Auth = () => {
  const [isLoginPage, setIsLoginPage] = useState(false);
  const [userNmae, setUserName] = useState({
    label: "User Nmae",
    value: "",
  });

  const title = (page) => {
    return page ? "Login Page" : "Registration Page";
  };

  const onChangeUserNmae = (value) => {
    setUserName({ ...userNmae, value: value });
  };

  const onChangePage = () => {
    console.log(";");
    setIsLoginPage(!isLoginPage);
  };

  return (
    <div className={styles.container_fluid}>
      <div className={styles.card}>
        <h1>{title(isLoginPage)}</h1>
        <div className={styles.form}>
          <Input
            type={"text"}
            onChange={onChangeUserNmae}
            value={userNmae.value}
            placeholder={userNmae.label}
            inputName={userNmae.label}
          />
        </div>
        <p onClick={onChangePage}>go to {title(!isLoginPage)}</p>
      </div>
    </div>
  );
};
