import React, { useEffect, useState } from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import { Auth } from "../pages/Auth";
import { ErrorPageNotFound } from "../pages/ErrorPageNotFound";
import { AuthData } from "../App";
import { Deshboar } from "../pages/Deshboar";

const routerData = [
  {
    path: "/auth",
    name: "Auth",
    element: <Auth />,
    isPrivate: false,
  },
  {
    path: "/deshboar",
    name: "Deshboar",
    element: <Deshboar />,
    isPrivate: true,
  },
];

export const Router = () => {
  const { user } = AuthData();
  const [routs, setRoutes] = useState(routerData?.filter((item) => {
    if (user?.isAuth && item.isPrivate) {
      return {
        path: item.path,
        element: item.element,
      };
    } else if (!user?.isAuth && !item.isPrivate) {
      return {
        path: item.path,
        element: item.element,
      };
    }
  }))

  useEffect(() => {}, [user]);

  const router = createBrowserRouter([
    ...routs,
    {
      path: "*",
      element: <ErrorPageNotFound />,
    },
  ]);

  return <RouterProvider router={router} />;
};
