import React, { useEffect, useState } from "react";
import { createBrowserRouter, RouterProvider, Navigate } from "react-router-dom";
import { AuthData } from "../App";
import { Auth } from "../pages/Auth";
import { AdminPage } from "../pages/AdminPage";
import { EmployeePage } from '../pages/EmployeePage';

const routerData = [
  {
    path: "/auth",
    name: "Auth",
    element: <Auth />,
    isPrivate: false,
  },
  {
    path: "/employee-page",
    name: "EmployeePage",
    element: <EmployeePage />,
    isPrivate: true,
    role: 'employee'
  },
  {
    path: "/admin-page",
    name: "AdminPage",
    element: <AdminPage />,
    isPrivate: true,
    role: 'administrator'
  },
];

export const Router = () => {
  const { user, getCurentUser } = AuthData();
  const [routs, setRoutes] = useState([]);

  const authGuard = async () => {
    const token = localStorage.getItem("token");
    if (token) {
      await getCurentUser();
    }
    setRoutes(
      routerData?.filter((item) => {
        if (user?.isAuth && item.isPrivate) {
          if (user?.role === item?.role) {
            return {
              path: item.path,
              element: item.element,
            }
          }
        } else if (!user?.isAuth && !item.isPrivate) {
          return {
            path: item.path,
            element: item.element,
          };
        }
      })
    );
  };

  useEffect(() => {
    authGuard();
  }, []);

  useEffect(() => {
  }, [user]);
  console.log(user)
  const router = createBrowserRouter([
    ...routs,
    {
      path: "*",
      element:
        user && user?.role === 'employee' ? <Navigate to="/employee-page" /> :
          user && user?.role === 'administrator' ? <Navigate to="/admin-page" /> :
            <Navigate to="/auth" />,
    },
  ]);

  return <RouterProvider router={router} />;
};
